import React, { useState } from 'react';
import argTypesEnum from '../../../../enums/argTypesEnum';
import Image from '../Image';
import { Button, Space } from 'antd';

export default {
  title: 'DataDisplay/Image',
  component: Image,
  argTypes: {
    alt: argTypesEnum.STRING,
    className: argTypesEnum.STRING,
    dataTestId: argTypesEnum.STRING,
    fallback: argTypesEnum.STRING,
    height: null,
    onError: argTypesEnum.EVENTS,
    placeholder: null,
    preview: null,
    src: argTypesEnum.STRING,
    width: null,
  },
};

const Template = (args) => (
  <Image
    {...args}
  />
);

const TemplateProgressiveLoading = () => {
  const [random, setRandom] = useState();
  return (
    <Space size={12}>
      <Image
        width={200}
        src={`https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?${random}`}
        placeholder={
          <Image
            preview={false}
            src="https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200"
            width={200}
          />
        }
      />
      <Button
        type="primary"
        onClick={() => {
          setRandom(Date.now());
        }}
      >
        Reload
      </Button>
    </Space>
  );
}

const TemplateImageMultiplePreview = () => {
  return (
    <Image.PreviewGroup>
      <Image width={200} src="https://gw.alipayobjects.com/zos/rmsportal/KDpgvguMpGfqaHPjicRK.svg" />
      <Image
        width={200}
        src="https://gw.alipayobjects.com/zos/antfincdn/aPkFc8Sj7n/method-draw-image.svg"
      />
    </Image.PreviewGroup>
  )
}

const TemplateImagePreviewFromOneImage = () => {
  const [visible, setVisible] = useState(false);
  return (
    <>
      <Image
        preview={{ visible: false }}
        width={200}
        src="https://gw.alipayobjects.com/zos/antfincdn/LlvErxo8H9/photo-1503185912284-5271ff81b9a8.webp"
        onClick={() => setVisible(true)}
      />
      <div style={{ display: 'none' }}>
        <Image.PreviewGroup preview={{ visible, onVisibleChange: vis => setVisible(vis) }}>
          <Image src="https://gw.alipayobjects.com/zos/antfincdn/LlvErxo8H9/photo-1503185912284-5271ff81b9a8.webp" />
          <Image src="https://gw.alipayobjects.com/zos/antfincdn/cV16ZqzMjW/photo-1473091540282-9b846e7965e3.webp" />
          <Image src="https://gw.alipayobjects.com/zos/antfincdn/x43I27A55%26/photo-1438109491414-7198515b166b.webp" />
        </Image.PreviewGroup>
      </div>
    </>
  );
}

export const ImageDefault = Template.bind({});
ImageDefault.args = {
  src: "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
  width: 200,
};

export const ImageFaultTolerant = Template.bind({});
ImageFaultTolerant.args = {
  width: 200,
  height: 200,
  src: "error",
  fallback: "data:image/png;base64,"
}

export const ImageProgressiveLoading = TemplateProgressiveLoading.bind({});
export const ImageMultiplePreview = TemplateImageMultiplePreview.bind({});
export const ImagePreviewFromOneImage = TemplateImagePreviewFromOneImage.bind({});

export const ImageCustomPreview = Template.bind({});
ImageCustomPreview.args = {
  width: 200,
  src: "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200",
  preview:{
    src: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
  },
}